// client/src/pages/Success.jsx
import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";

function Success() {
  const [searchParams] = useSearchParams();
  const [session, setSession] = useState(null);
  const [message, setMessage] = useState("Loading payment details...");

  useEffect(() => {
    const sessionId = searchParams.get("session_id");
    if (!sessionId) {
      setMessage("No session found.");
      return;
    }

    const fetchSession = async () => {
      try {
        // ✅ Call backend → backend will also save the order in DB
        const res = await fetch(`http://localhost:4000/v1/checkout/session/${sessionId}`);
        const data = await res.json();

        if (res.ok) {
          setSession(data);
          setMessage("✅ Payment successful! Your order has been placed.");
        } else {
          setMessage("❌ Failed to confirm order.");
        }
      } catch (err) {
        console.error("❌ Error fetching session:", err);
        setMessage("❌ Something went wrong.");
      }
    };

    fetchSession();
  }, [searchParams]);

  if (!session) {
    return (
      <div style={{ padding: 20, textAlign: "center" }}>
        <h2>{message}</h2>
      </div>
    );
  }

  // ✅ Build full address string safely
  const address = session.customer_details?.address
    ? [
        session.customer_details.address.line1,
        session.customer_details.address.line2,
        session.customer_details.address.city,
        session.customer_details.address.state,
        session.customer_details.address.postal_code,
        session.customer_details.address.country,
      ]
        .filter(Boolean)
        .join(", ")
    : "Not provided";

  return (
    <div style={{ padding: 20, maxWidth: 600, margin: "0 auto" }}>
      <h2>✅ Payment Successful!</h2>
      <p style={{ color: "green" }}>{message}</p>

      <h3>Invoice</h3>
      <p>
        <b>Session ID:</b> {session.id}
      </p>
      <p>
        <b>Total Paid:</b> ₹{session.amount_total / 100}
      </p>

      {/* ✅ Customer Details */}
      <h4>Customer Details</h4>
      <p>
        <b>Name:</b> {session.customer_details?.name}
      </p>
      <p>
        <b>Email:</b> {session.customer_details?.email}
      </p>
      <p>
        <b>Phone:</b> {session.customer_details?.phone || "Not provided"}
      </p>
      <p>
        <b>Address:</b> {address}
      </p>

      {/* ✅ Products */}
      <h4>Products</h4>
      {session.line_items?.data.map((item, idx) => (
        <p key={idx}>
          {item.description} (x{item.quantity}) - ₹{item.amount_total / 100}
        </p>
      ))}
    </div>
  );
}

export default Success;
