import { useState } from "react";
import { useCart } from "../context/CartContext";
import stripePromise from "../stripe";

export default function Checkout() {
  const { cartItems } = useCart();
  const [loading, setLoading] = useState(false);

  const handleCheckout = async () => {
    if (!cartItems || cartItems.length === 0) {
      alert("Cart is empty!");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch("http://localhost:4000/v1/checkout/sessions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ items: cartItems }),
      });

      if (!response.ok) throw new Error("Failed to create session");

      const { sessionId } = await response.json();
      const stripe = await stripePromise;
      await stripe.redirectToCheckout({ sessionId });
    } catch (err) {
      console.error("Checkout error:", err);
      alert("❌ Error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  // Total amount calculation
  const totalAmount = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="flex flex-col md:flex-row items-start justify-center py-16 gap-10 px-4 md:px-20">
      
      {/* Left side: Product Image */}
      <div className="flex-1 max-w-md">
        {cartItems.length > 0 && (
          <img
            src={cartItems[0].image || "https://via.placeholder.com/400x300"}
            alt={cartItems[0].name}
            className="w-full h-auto rounded-lg shadow-lg"
          />
        )}
      </div>

      {/* Right side: Order Summary & Checkout */}
      <div className="flex-1 max-w-md bg-white shadow-lg rounded-lg p-6">
        <h2 className="text-2xl font-bold mb-4">Order Summary</h2>

        {cartItems && cartItems.length > 0 ? (
          <div className="space-y-3">
            {cartItems.map((item, idx) => (
              <div key={idx} className="flex justify-between items-center">
                <div>
                  <p className="font-medium">{item.name}</p>
                  <p className="text-sm text-gray-500">
                    Qty {item.quantity} × ${item.price.toFixed(2)}
                  </p>
                </div>
                <p className="font-semibold">${(item.price * item.quantity).toFixed(2)}</p>
              </div>
            ))}
            <hr className="my-3" />
            <div className="flex justify-between font-bold text-lg">
              <span>Total</span>
              <span>${totalAmount.toFixed(2)}</span>
            </div>
          </div>
        ) : (
          <p>No items in cart.</p>
        )}

        {/* Pay Button */}
        <button
          onClick={handleCheckout}
          disabled={loading || !cartItems || cartItems.length === 0}
          className={`mt-6 w-full bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition ${
            loading ? "cursor-not-allowed opacity-70" : ""
          }`}
        >
          {loading ? "Redirecting to Stripe..." : "Pay Now"}
        </button>
      </div>
    </div>
  );
}
