import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import productsData from "../data/products"; // <-- local products file

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    setProducts(productsData); // directly load local products
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2>Products</h2>
      <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(240px, 1fr))", gap: "20px" }}>
        {products.map((product) => (
          <div key={product.id} style={{ border: "1px solid #ddd", borderRadius: "10px", padding: "15px", textAlign: "center" }}>
            <img
              src={product.image}
              alt={product.name}
              style={{ width: "100%", height: "180px", objectFit: "contain", cursor: "pointer" }}
              onClick={() => navigate(`/product/${product.id}`)}
            />
            <h3>{product.name}</h3>
            <p>({product.pieces} Pieces)</p>
            <p style={{ color: "red", fontWeight: "bold" }}>₹{product.price}</p>
            <p style={{ textDecoration: "line-through", color: "gray" }}>MRP: ₹{product.mrp}</p>
            <p>⭐ {product.rating}</p>
            <button onClick={() => addToCart(product, 1)} style={{ padding: "8px 15px", background: "tomato", color: "white", border: "none", borderRadius: "6px", cursor: "pointer" }}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
