import { useLocation } from "react-router-dom";

function Checkout() {
  const location = useLocation();
  const { cartItems = [], totalAmount = 0 } = location.state || {};

  const handleStripePayment = async () => {
    try {
      const response = await fetch("http://localhost:4000/v1/checkout/sessions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ cartItems, currency: "usd" }),
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error || "Failed to create session");

      // redirect to Stripe Checkout
      window.location.href = data.url;
    } catch (err) {
      console.error("Checkout error", err);
      alert("Checkout failed: " + err.message);
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: "0 auto", padding: 20 }}>
      <h2>Checkout</h2>
      {cartItems.map((item) => (
        <div key={item.id} style={{ display: "flex", justifyContent: "space-between", marginBottom: 10 }}>
          <div>{item.name} (x{item.quantity || 1})</div>
          <div>₹{(item.price * (item.quantity || 1)).toFixed(2)}</div>
        </div>
      ))}
      <hr />
      <h3>Total: ₹{totalAmount.toFixed(2)}</h3>
      <button
        onClick={handleStripePayment}
        style={{ marginTop: 20, padding: "10px 20px", background: "green", color: "white" }}
      >
        Pay with Stripe
      </button>
    </div>
  );
}

export default Checkout;
