import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const navigate = useNavigate();
  const { addToCart } = useCart();

  useEffect(() => {
    fetch("http://localhost:4000/api/products")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched products:", data); // Debug
        setProducts(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Error fetching products:", err);
        setLoading(false);
      });
  }, []);

  if (loading) return <h2 style={{ padding: "20px" }}>Loading products...</h2>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Products</h2>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fill, minmax(240px, 1fr))",
          gap: "20px",
        }}
      >
        {products.map((product) => (
          <div
            key={product._id || product.id}
            style={{
              border: "1px solid #ddd",
              borderRadius: "10px",
              padding: "15px",
              textAlign: "center",
              background: "#fff",
              boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
            }}
          >
            <img
              src={product.image}
              alt={product.title}
              style={{
                width: "100%",
                height: "180px",
                objectFit: "contain",
                cursor: "pointer",
              }}
              onClick={() => navigate(`/product/${product._id || product.id}`)}
            />

            <h3>{product.title}</h3>
            <p>({product.pieces} Pieces)</p>
            <p style={{ color: "red", fontWeight: "bold" }}>₹{product.price}</p>
            <p style={{ textDecoration: "line-through", color: "gray" }}>
              MRP: ₹{product.mrp}
            </p>
            <p>⭐ {product.rating}</p>

            <div style={{ margin: "10px 0" }}>
              <button>-</button>
              <span style={{ margin: "0 10px" }}>1</span>
              <button>+</button>
            </div>

            <button
              onClick={() => addToCart(product, 1)}
              style={{
                padding: "8px 15px",
                background: "tomato",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
