import { useParams } from "react-router-dom";
import products from "../data/products"; // unga products.js import

function ProductDetailPage() {
  const { id } = useParams(); // URL la iruka id eduthukkum
  const product = products.find((p) => p.id === Number(id)); // number ah match pannanum

  if (!product) {
    return <h2>Product not found ❌</h2>;
  }

  return (
    <div
      style={{
        padding: "20px",
        maxWidth: "600px",
        margin: "auto",
        textAlign: "center",
      }}
    >
      <h2>{product.name}</h2>
      <img
        src={product.image}
        alt={product.name}
        style={{ width: "300px", height: "300px", objectFit: "contain" }}
      />
      <p style={{ fontSize: "18px", margin: "10px 0" }}>₹{product.price}</p>
      <p>{product.description || "No description available."}</p>
    </div>
  );
}

export default ProductDetailPage;
