import { Link, useLocation } from "react-router-dom";
import { useSearch } from "../context/SearchContext";

const Navbar = () => {
  const location = useLocation();
  const { search, setSearch } = useSearch();

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "15px 30px",
        backgroundColor: "#222",
        color: "#fff",
      }}
    >
      {/* ðŸ”¹ Logo */}
      <Link
        to="/"
        style={{
          color: "#fff",
          fontSize: "22px",
          fontWeight: "bold",
          textDecoration: "none",
        }}
      >
        Crackers Shop
      </Link>

      {/* ðŸ”¹ Links */}
      <div>
        <Link to="/" style={{ margin: "0 15px", color: "#fff", textDecoration: "none" }}>Home</Link>
        <Link to="/products" style={{ margin: "0 15px", color: "#fff", textDecoration: "none" }}>Products</Link>
        <Link to="/cart" style={{ margin: "0 15px", color: "#fff", textDecoration: "none" }}>Cart</Link>
      </div>

      {/* ðŸ”¹ Search Box â†’ Only in /products */}
      {location.pathname === "/products" && (
        <input
          type="text"
          value={search}
          onChange={handleSearch}
          placeholder="Search products..."
          style={{
            padding: "6px 12px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            width: "250px",
          }}
        />
      )}
    </nav>
  );
};

export default Navbar;
