import { useEffect, useState } from "react";

function AdminDashboard() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token");

  useEffect(() => {
    fetch("http://localhost:4000/api/admin/orders", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then((data) => {
        if (Array.isArray(data)) {
          setOrders(data);
        } else {
          setOrders([]);
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error("Admin fetch error:", err);
        setLoading(false);
      });
  }, [token]);

  if (loading) return <div className="container mt-4">Loading...</div>;

  return (
    <div className="container mt-4">
      <h2 className="fw-bold mb-4">📦 Orders Timeline</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <div className="d-flex flex-column gap-4">
          {orders.map((o) => (
            <div
              key={o._id}
              className="p-4 rounded shadow-sm border-start border-4 border-primary"
              style={{ background: "#fafafa" }}
            >
              <div className="d-flex justify-content-between align-items-center mb-2">
                <h5 className="mb-0 text-primary">Order #{o._id.slice(-6)}</h5>
                <small className="text-muted">
                  {new Date(o.createdAt).toLocaleString()}
                </small>
              </div>

              <p className="mb-1">
                <strong>👤 {o.customerName}</strong> <br />
                <small>{o.customerEmail}</small> <br />
                <small>{o.customerPhone}</small>
              </p>

              <p className="mb-1">
                <strong>📍 Address:</strong> <br />
                {o.customerAddress?.line1}, {o.customerAddress?.city},{" "}
                {o.customerAddress?.state} - {o.customerAddress?.postal_code},{" "}
                {o.customerAddress?.country}
              </p>

              <div className="mb-2">
                <strong>🛒 Products:</strong>
                <ul className="mb-0">
                  {Array.isArray(o.items) &&
                    o.items.map((p, i) => (
                      <li key={i}>
                        {p.name} (x{p.quantity}) – ₹{p.price}
                      </li>
                    ))}
                </ul>
              </div>

              <div className="d-flex justify-content-between align-items-center">
                <span className="fw-bold text-success fs-5">
                  💰 Total: ₹{o.totalAmount}
                </span>
                <small
                  style={{
                    fontSize: "12px",
                    wordBreak: "break-word",
                    maxWidth: "300px",
                  }}
                  className="text-muted"
                >
                  Session ID: {o.stripeSessionId}
                </small>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
