import { loadStripe } from "@stripe/stripe-js";
import { useState } from "react";

//Load stripe services
const stripe = loadStripe(import.meta.env.VITE_STRIPE_API_KEY);

function App() {
  const [session, setSession] = useState("");

  //Trigger : Stripe API - checkout loader
  const handleStripePayment = async (e) => {
    e.preventDefault(); // Prevent form submission

    try {
      const response = await fetch(
        "http://localhost:4000/v1/checkout/sessions",
        { method: "POST" }
      );
      //validation
      if (!response.ok) {
        throw new Error("Failed to get session id");
      }
      const data = await response.json();
      setSession(data); // store api response to state

      const stripeInstance = await stripe;
      await stripeInstance?.redirectToCheckout({ sessionId: data.sessionId });
    } catch (error) {
      alert("Error:", error?.message);
    }
  };

  return (
    <>
      <section className="flex justify-center items-center">
        <form onSubmit={handleStripePayment} className="py-48">
          <button
            type="submit"
            className="bg-emerald-500 px-5 py-2.5 rounded text-white"
          >
            Pay Now
          </button>
        </form>
      </section>
    </>
  );
}

export default Checkout;
