import { loadStripe } from "@stripe/stripe-js";
import { useLocation } from "react-router-dom";
import { useState } from "react";

// Stripe public key (publishable key)
console.log("Stripe key üëâ", process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY);



function Checkout() {
  const location = useLocation();
  const { cartItems, totalAmount } = location.state || { cartItems: [], totalAmount: 0 };
  const [loading, setLoading] = useState(false);

  const handleStripePayment = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await fetch("http://localhost:4000/v1/checkout/sessions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ cartItems, totalAmount }),
      });

      if (!response.ok) {
        throw new Error("Server error while creating session");
      }

      const data = await response.json();

      if (data.url) {
        // üîπ Instead of window.location.href (old way),
        // use Stripe redirect for safety
        const stripe = await stripePromise;
        stripe.redirectToCheckout({ sessionId: data.id });
      } else {
        alert("‚ùå Checkout failed: " + (data.error || "Unknown error"));
      }
    } catch (err) {
      alert("Checkout failed: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: "30px" }}>
      <h2>Checkout</h2>

      {cartItems.length === 0 ? (
        <p>No items in cart.</p>
      ) : (
        <>
          {cartItems.map((item, index) => (
            <p key={index}>
              {item.name} ({item.quantity}x) - ‚Çπ{item.price * item.quantity}
            </p>
          ))}

          <h3>Total: ‚Çπ{totalAmount}</h3>

          <button
            onClick={handleStripePayment}
            disabled={loading}
            style={{
              background: "green",
              color: "white",
              padding: "10px 20px",
              border: "none",
              cursor: "pointer",
              marginTop: "15px",
            }}
          >
            {loading ? "Processing..." : "Pay with Stripe"}
          </button>
        </>
      )}
    </div>
  );
}

export default Checkout;
