import { useState } from "react";
import { useCart } from "../context/CartContext";
import stripePromise from "../stripe";      // ✅ correct


export default function Checkout() {
  const { cartItems } = useCart();
  const [loading, setLoading] = useState(false);

  const handleCheckout = async () => {
    if (!cartItems || cartItems.length === 0) {
      alert("Cart is empty!");
      return;
    }

    setLoading(true);
    try {
      const response = await fetch("http://localhost:4000/v1/checkout/sessions", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ items: cartItems }),
      });

      if (!response.ok) throw new Error("Failed to create session");

      const { sessionId } = await response.json();
      const stripe = await stripePromise;
      await stripe.redirectToCheckout({ sessionId });
    } catch (err) {
      console.error("Checkout error:", err);
      alert("❌ Error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center py-24">
      <h1 className="text-2xl font-bold mb-6">Checkout</h1>

      <div className="bg-gray-100 p-6 rounded-lg w-[400px] mb-6">
        <h2 className="text-lg font-semibold mb-4">Order Summary</h2>
        {cartItems && cartItems.length > 0 ? (
          <ul>
            {cartItems.map((item, idx) => (
              <li key={idx} className="flex justify-between mb-2">
                <span>{item.name} × {item.quantity}</span>
                <span>${item.price * item.quantity}</span>
              </li>
            ))}
          </ul>
        ) : (
          <p>No items in cart.</p>
        )}
      </div>

      <button
        onClick={handleCheckout}
        disabled={loading || !cartItems || cartItems.length === 0}
        className="bg-emerald-600 text-white px-6 py-2 rounded-lg hover:bg-emerald-700"
      >
        {loading ? "Processing..." : "Proceed to Purchase"}
      </button>
    </div>
  );
}
