// client/src/pages/admin/AdminDashboard.jsx
import { useEffect, useState } from "react";

function AdminDashboard() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    fetch("http://localhost:4000/api/orders", {
      headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
    })
      .then(res => res.json())
      .then(data => setOrders(data));
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h2>Admin Dashboard</h2>
      <h3>Orders</h3>
      {orders.map((o) => (
        <div key={o._id} style={{ border: "1px solid #ccc", margin: "10px", padding: "10px" }}>
          <p><b>Customer:</b> {o.customerName} ({o.customerEmail}, {o.customerPhone})</p>
          <p><b>Address:</b> {o.customerAddress?.line1}, {o.customerAddress?.city}</p>
          <p><b>Total:</b> ₹{o.totalAmount}</p>
          <h4>Items:</h4>
          <ul>
            {o.items.map((it, idx) => (
              <li key={idx}>{it.description} — {it.quantity}x</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default AdminDashboard;
