import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";

function Success() {
  const [searchParams] = useSearchParams();
  const [session, setSession] = useState(null);

  useEffect(() => {
    const sessionId = searchParams.get("session_id");
    if (sessionId) {
      fetch(`http://localhost:4000/v1/checkout/session/${sessionId}`)
        .then((res) => res.json())
        .then((data) => setSession(data))
        .catch((err) => console.error("‚ùå Error fetching session:", err));
    }
  }, [searchParams]);

  if (!session) {
    return <h3 style={{ padding: 20 }}>Loading your receipt...</h3>;
  }

  return (
    <div style={{ padding: "30px", maxWidth: "600px", margin: "0 auto" }}>
      <h2 style={{ color: "green" }}>‚úÖ Payment Successful!</h2>
      <p><strong>Session ID:</strong> {session.id}</p>
      <p><strong>Email:</strong> {session.customer_details?.email}</p>
      <p><strong>Total Paid:</strong> ‚Çπ{session.amount_total / 100}</p>

      <h3>üßæ Purchased Items</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {session.line_items?.data.map((item, idx) => (
          <li
            key={idx}
            style={{
              borderBottom: "1px solid #ddd",
              padding: "10px 0",
              display: "flex",
              justifyContent: "space-between",
            }}
          >
            <span>{item.description} (x{item.quantity})</span>
            <span>‚Çπ{item.amount_total / 100}</span>
          </li>
        ))}
      </ul>

      <h3 style={{ marginTop: "20px" }}>üéâ Thank you for your purchase!</h3>
    </div>
  );
}

export default Success;
