// client/src/pages/AdminDashboard.jsx
import React, { useEffect, useState } from "react";

function AdminDashboard() {
  const [data, setData] = useState({ users: 0, orders: 0, revenue: 0, recentOrders: [] });
  const [loading, setLoading] = useState(true);
  const token = localStorage.getItem("token"); // ensure you store token after login

  useEffect(() => {
    async function fetchDashboard() {
      try {
        const res = await fetch("http://localhost:4000/api/admin/dashboard", {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        });
        if (!res.ok) {
          const err = await res.json();
          throw new Error(err.msg || "Failed to fetch");
        }
        const json = await res.json();
        setData(json);
      } catch (err) {
        console.error("Admin fetch error:", err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchDashboard();
  }, [token]);

  if (loading) return <div>Loading admin data...</div>;

  return (
    <div className="container p-4">
      <h2>Admin Dashboard</h2>
      <div className="row my-3">
        <div className="col-md-4 card p-3 m-2">
          <h5>Total Users</h5>
          <p>{data.users}</p>
        </div>
        <div className="col-md-4 card p-3 m-2">
          <h5>Total Orders</h5>
          <p>{data.orders}</p>
        </div>
        <div className="col-md-4 card p-3 m-2">
          <h5>Total Revenue</h5>
          <p>₹{data.revenue}</p>
        </div>
      </div>

      <h4 className="mt-4">Recent Orders</h4>
      <div>
        {data.recentOrders && data.recentOrders.length ? (
          <table className="table">
            <thead>
              <tr>
                <th>Order Id</th>
                <th>User</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {data.recentOrders.map((o) => (
                <tr key={o._id}>
                  <td>{o._id}</td>
                  <td>{o.user ? o.user.name || o.user : o.user}</td>
                  <td>₹{o.amount ?? o.totalAmount ?? "—"}</td>
                  <td>{o.status ?? "—"}</td>
                  <td>{new Date(o.createdAt).toLocaleString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No recent orders</p>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
